import React from 'react';

const Operacion = ({ operacion }) => {

    const { concepto, monto, tipo, categoria } = operacion;
    let { fecha } = operacion;

    fecha = operacion.fecha.substring(0,10);

    return (
        <div className="grid grid-cols-7 mb-2 items-center">
            <h2 className="font-bold text-blue-500">{tipo}</h2>
            <h2>{categoria}</h2>
            <h2 className="font-bold">{concepto}</h2>
            <h2 className="font-normal">${monto}</h2>
            <h2 className="italic">{fecha}</h2>
        
            <button className="border rounded-lg hover:bg-black lg:mt-0 bg-blue-400 mr-3 px-5 py-1 text-white font-bold uppercase">Editar</button>
            <button className="border rounded-lg hover:bg-black lg:mt-0 bg-blue-400 mr-3 px-5 py-1 text-white font-bold uppercase">Editar</button>

        </div>
    );
}

export default Operacion;
