import React from 'react';

const Operacion = ({ operacion }) => {

    const { concepto, monto, tipo, categoria } = operacion;
    let { fecha } = operacion;

    fecha = operacion.fecha.substring(0,10);

    return (
        <div className="grid auto-cols-min	 mb-2 items-center">
            <h2 className="font-bold text-blue-500 uppercase">{tipo}</h2>
            <h2>{categoria}</h2>
            <h2 className="font-bold">{concepto}</h2>
            <h2 className="font-normal">${monto}</h2>
            <h2 className="italic">{fecha}</h2>
        
            <img 
                onClick={() => redireccionar() }
                className="w-8 cursor-pointer" src="/editar.png" 
            />

            <img 
                onClick={() => redireccionar() }
                className="w-8 cursor-pointer" src="/eliminar.png" 
            />
         
        </div>
    );
}

export default Operacion;
