import React, { useContext, useEffect } from 'react';
import Link from 'next/link';
import authContext from '../context/auth/authContext';
import appContext from '../context/app/appContext';
import { useRouter } from 'next/router';


const Header = () => {

    // routing 
    const router = useRouter();

      // Extraer el Usuario autenticado del Storage 
    const AuthContext = useContext( authContext );
    const { usuario, usuarioAutenticado,  cerrarSesion } = AuthContext;

      // Context de la aplicación
    const AppContext = useContext( appContext );
    const { limpiarState } = AppContext;

    useEffect(() => {
        usuarioAutenticado()
    }, []);

    const redireccionar = () => {
        router.push('/');
        limpiarState();
    }

    const mostrarMenu = () => {
        menu.classList.toggle('hidden');
    }

    return ( 
        <header className="bg-gray-200 py-8 flex flex-col md:flex-row items-center justify-around">
            <img 
                onClick={() => redireccionar() }
                className="w-64 mb-8 md:mb-0 cursor-pointer" src="/logo.png" 
            />

            <div class="block lg:hidden">
                <button
                    id="boton"
                    class="flex items-center px-3 py-2 border rounded text-teal-200 border-teal-400 hover:text-white hover:border-white"
                    onClick={() => mostrarMenu() }    
                >
                <svg class="fill-current h-3 w-3" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><title>Menu</title><path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z"/></svg>
                </button>
            </div>

            <div id="menu" className="flex flex-wrap items-center">
                <div class="lg:flex-grow text-black font-bold px-5 py-3 mr-2">
                    <Link href="/registraroperacion">
                        <a className=" hover:text-blue-400">Registrar Operación</a>
                    </Link>
                    <Link href="/listado">
                        <a className=" hover:text-blue-400">Listado</a>
                    </Link>
                </div>

                    <p className="px-5">Hola usuario.nombre{/*usuario.nombre*/}</p>
                    <button 
                        type="button"
                        className="bg-blue-400 px-5 py-3 rounded-lg text-white font-bold uppercase"
                        onClick={() => cerrarSesion() }
                    >Cerrar Sesión</button>
            </div>





        </header>
     );
}

export default Header;
