import React, { useContext, useEffect } from 'react';
import Link from 'next/link';
import authContext from '../context/auth/authContext';
import { useRouter } from 'next/router';

const Header = () => {

    // routing 
    const router = useRouter();

      // Extraer el Usuario autenticado del Storage 
    const AuthContext = useContext( authContext );
    const { usuario, usuarioAutenticado,  cerrarSesion } = AuthContext;

    useEffect(() => {
        usuarioAutenticado();
    }, []);

    const redireccionar = () => {
        router.push('/');
    }

    const mostrarMenu = () => {
        menu.classList.toggle('hidden');
    }

    return (

<nav class="flex items-center justify-between flex-wrap bg-gray-300 p-6">

    <div class="flex items-center flex-shrink-0 text-white mr-6">
            <svg class="fill-current h-8 w-8 mr-2" width="54" height="54" viewBox="0 0 54 54" xmlns="http://www.w3.org/2000/svg"><path d="M13.5 22.1c1.8-7.2 6.3-10.8 13.5-10.8 10.8 0 12.15 8.1 17.55 9.45 3.6.9 6.75-.45 9.45-4.05-1.8 7.2-6.3 10.8-13.5 10.8-10.8 0-12.15-8.1-17.55-9.45-3.6-.9-6.75.45-9.45 4.05zM0 38.3c1.8-7.2 6.3-10.8 13.5-10.8 10.8 0 12.15 8.1 17.55 9.45 3.6.9 6.75-.45 9.45-4.05-1.8 7.2-6.3 10.8-13.5 10.8-10.8 0-12.15-8.1-17.55-9.45-3.6-.9-6.75.45-9.45 4.05z"/></svg>
            <span class="font-semibold text-xl text-black tracking-tight">ALKEMY</span>
    </div>

    <div class="block lg:hidden mr-6">
        <button id='boton' class="flex items-center px-3 py-2 border rounded text-teal-200 border-teal-400 hover:text-white hover:border-white">
        <svg class="fill-current h-3 w-3" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><title>Menu</title><path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z"/></svg>
        </button>
    </div>

    <div id='menu' class="w-full block flex-grow lg:flex lg:items-center lg:w-auto">

        <div class="lg:flex-grow text-black font-bold">


            <Link href="/registraroperacion">
                <a className={router.pathname === "/registraroperacion"
                    ? "block py-4 lg:inline-block lg:mt-0 mr-10 py-2 hover:text-blue-400 underline"
                    : "block py-4 lg:inline-block lg:mt-0 mr-10 py-2 hover:text-blue-400"
                }>Registrar Operación</a>
            </Link>

            <Link href="/listado">
                <a className={router.pathname === "/listado"
                    ? "block py-4 lg:inline-block lg:mt-0 mr-10 py-2 hover:text-blue-400 underline"
                    : "block py-4 lg:inline-block lg:mt-0 mr-10 py-2 hover:text-blue-400"
                }>Listado</a>
            </Link>








        </div>

        <div>
            <a href="/login" className="bg-blue-400 inline-block text-sm text-white font-bold uppercase px-4 py-2  rounded-lg
                 hover:bg-black mt-4 lg:mt-0 lg:px-5 lg:py-3"

                onClick={() => cerrarSesion()}>Cerrar Sesión</a>
        </div>



    </div>

</nav>





     );
}

export default Header;
