import React, { useContext, useEffect } from 'react';
import Layout from '../components/Layout';
import authContext from '../context/auth/authContext';
import { useRouter } from 'next/router';

const Index = () => {

    // routing 
    const router = useRouter();

    // Extraer el Usuario autenticado del Storage 
    const AuthContext = useContext( authContext );
    const { usuario, usuarioAutenticado } = AuthContext;

    useEffect(() => {
      const token = localStorage.getItem('token');

      if(token) {
        usuarioAutenticado()
      } else {
        router.push('/login');
      }

    }, []);

    return ( 
        <div>
            {
                usuario ? (
                  <Layout>
                      <div className="md:w-4/5 xl:w-3/5 mx-auto">
                        <h3 className="text-3xl font-sans font-bold text-black-500 text-center my-4"
                        >Balance de operaciones de
                            <span className="text-blue-500"> {usuario.nombre}</span>
                        </h3>

                        <div className="bg-gray-300 rounded-lg px-5 py-2">
                            <p className="flex-1 font-bold text-blue-500 uppercase">Ingresos:
                                <span className="font-normal text-black normal-case">Acá va la suma de ingresos</span>
                            </p>
                            <p className="flex-1 font-bold text-blue-500 uppercase">Egresos: </p>
                            <p className="flex-1 font-bold text-blue-500 uppercase">Balance: </p>
                        </div>
                      </div>
                  </Layout>
                ) : (
                    <>

                    </>
                )
            }

        </div>
  );
}

export default Index;
