import React, { useContext, useEffect } from 'react';
import Link from 'next/link';
import authContext from '../context/auth/authContext';
import appContext from '../context/app/appContext';
import { useRouter } from 'next/router';


const Header = () => {

    // routing 
    const router = useRouter();

      // Extraer el Usuario autenticado del Storage 
    const AuthContext = useContext( authContext );
    const { usuario, usuarioAutenticado,  cerrarSesion } = AuthContext;

      // Context de la aplicación
    const AppContext = useContext( appContext );
    const { limpiarState } = AppContext;

    useEffect(() => {
        usuarioAutenticado()
    }, []);

    const redireccionar = () => {
        router.push('/');
        limpiarState();
    }


    return ( 
        <header className="py-8 flex flex-col md:flex-row items-center justify-around">


            <div>
                {
                    usuario ? (
                        <div className="flex items-center">


                            <p className="mr-2">Hola {usuario.nombre}</p>
                            <button 
                                type="button"
                                className="bg-black px-5 py-3 rounded-lg text-white font-bold uppercase"
                                onClick={() => cerrarSesion() }
                            >Cerrar Sesión</button>
                        </div>
                    ) : (
                        <>
                    <nav class="flex items-center justify-between flex-wrap bg-red-500 p-6">

                        <img 
                            onClick={() => redireccionar() }
                            className="w-64 mb-8 md:mb-0 cursor-pointer" src="/logo.png"  viewBox="0 0 54 54"
                        />

                        <div id='menu' class="w-full block flex-grow lg:flex lg:items-center lg:w-auto">
                            <div class="lg:flex-grow">
                                <Link href="/registraroperacion">
                                    <a className="block bg-blue-400 px-5 py-3 rounded-lg text-white font-bold uppercase mr-2">Registrar Operación</a>
                                </Link>
                                <Link href="/listado">
                                    <a className="block bg-blue-400 px-5 py-3 rounded-lg text-white font-bold uppercase mr-2">Listado</a>
                                </Link>
                            </div>
                        </div>
                                <Link href="/login">
                                    <a className="bg-black px-5 py-3 rounded-lg text-white font-bold uppercase mr-2">Iniciar Sesión</a>
                                </Link>
                                <Link href="/crearcuenta">
                                    <a className="bg-black px-5 py-3 rounded-lg text-white font-bold uppercase">Crear Cuenta</a>
                                </Link>
                            </nav>
                        </>
                    )
                }
            </div>


        </header>
     );
}

export default Header;
