import React from 'react';

const Operacion = ({ operacion }) => {

    const { concepto, monto, tipo, categoria } = operacion;
    let { fecha } = operacion;

    fecha = operacion.fecha.substring(0,10);

    return (
        <div className="grid grid-cols-4">
            <h2><span className="font-bold text-blue-500">{tipo}: </span>{categoria}</h2>
            <h2 className="font-bold">{concepto}: <span className="font-normal">${monto}</span></h2>
            <h2 className="italic">{fecha}</h2>
        
        </div>
    );
}

export default Operacion;
