import React from 'react';

const Operacion = ({ operacion }) => {

    const { concepto, monto, tipo, categoria } = operacion;
    let { fecha } = operacion;

    fecha = operacion.fecha.substring(0,10);

    return (
        <div className="grid">
            <h2>
                <span className="font-bold text-blue-500">{tipo}:</span>
                {categoria}
                <span className="font-bold">{concepto}:</span>
                ${monto} - {fecha}
            </h2>
        
        </div>
    );
}

export default Operacion;
